(function($){jQuery.fn.clickbox=function(options){var defaults={hoverclass:'hover',style:{cursor:'pointer'}};var opts=jQuery.extend(defaults,options);return this.each(function(){$(this).css(opts.style).mouseenter(function(){$(this).addClass(opts.hoverclass);}).mouseleave(function(){$(this).removeClass(opts.hoverclass);}).click(function(e){$target=$(e.target).is('a')?$(e.target):$('a:first',this);if(!$target.data('clickboxed')){e.preventDefault();$target.data('clickboxed',true).click();}else{$target.data('clickboxed',false);}}).find('a').click(function(e){if(!e.isDefaultPrevented()){e.preventDefault();if($(this).attr('target')==='_blank'){window.open($(this).attr('href'));}else{window.location=$(this).attr('href');}}});});};})(jQuery);